// src/About.js
import React from 'react';
import './About.css'; 

const About = () => {
  return (
    <div className="about-section">
      <h2>About Us</h2>
      <p>
      Harnessing the power of AI, our platform delivers real-time disaster management precautions and vital information to help you stay safe during emergencies.
      </p>
      <p>
      Stay ahead of natural disasters with our AI-driven insights, offering personalized safety guidelines and up-to-date information tailored to your specific situation.
      </p>
      <p>
      Our AI model provides critical disaster management strategies and precautionary measures, ensuring you are prepared and informed when it matters most.
      </p>
    </div>
  );
};

export default About;
